import React, {useState} from 'react'
import auth0 from '../lib/auth0'


const CreateStatus = () => {

    const [dados, setDados] = useState({
        status: 'bem',
        coords: {
            lat: null,
            long: null
        }
    })

    

    const getMyLocation = () => {
        if(navigator.geolocation){
            navigator.geolocation.getCurrentPosition(position => {
                console.log(position.coords);
                                
            })
        }
    }


    return (
        <div>
            <h1>Create Status</h1>

            <label className='block'>
                <input type="radio" name="status" id="bem"/> Estou bem
            </label>
            <label className='block'>
                <input type="radio" name="status" id="gripe"/> Estou com sintomas de gripe
            </label>
            <label className='block'>
                <input type="radio" name="status" id="covid"/> Estou com sintomas da coronga
            </label>

            <button onClick={getMyLocation}>Get My Location</button>
            Sua posição Atual: {lat}, {long}
        </div>
    );

}

export default CreateStatus;


export async function getServerSideProps({ req, res }){
    const session = await auth0.getSession(req);

    if(session){
        return {
            props:{
            user: session.user,
            isAuth: true
            }
        }
    }

    return {
        props:{
            isAuth: false,
            user:{
            }
        }

    }

}
