import React, {useEffect} from 'react'
import auth0 from '../lib/auth0'
import router from 'next/router'
import {db} from '../lib/db'

const App = (props) => {
    useEffect(() => {
        if(!props.isAuth){
            router.push('/');
        }else if(props.forceCreate){
            router.push('/create-status');
        }
    })
    if(!props.isAuth || props.forceCreate){
        return null;
    }   
    return (
        <div>
            <h1>App</h1>
            <pre>{JSON.stringify(props, null, 2)}</pre>
        </div>
    );

}


export default App;

export async function getServerSideProps({ req, res }){
    const session = await auth0.getSession(req);
    console.log(session);

    if(session){
        const today = new Date();
        const currentDate = today.getFullYear()+'-'+today.getMonth()+'-'+today.getDay();

        const checkToday = await db.collection('markers')
          .doc(currentDate)
          .collection('checks')
          .doc(session.user.sub)
          .get();


        const todayData = checkToday.data();
        let forceCreate = true;

        if(todayData){
            //pode ver outros checkins
            forceCreate = false;
            const checkins  = await db
            .collection('markers')
            .doc(currentDate)
            .collection('checks')
            .near({
                center: todayData.coordinates,
                radius: 1000
            })
            .get();
            checkins.docs.forEach(element => {
                console.log(element.id, element.data());
                
            });
            
        }
        

        return {
            props:{
               user: session.user,
               isAuth: true,
               forceCreate
            }
        }
    }
    
    return {
        props:{
            isAuth: false,
            user:{
            }
        }

    }

}
